    [TestClass]
    public class UploadRequestTests
    {
        // ReSharper disable once UnusedAutoPropertyAccessor.Local
        public TestContext TestContext { get; set; }

        private IServiceProvider serviceProvider;

        [TestInitialize]
        public void Initialize()
        {
            serviceProvider = ContainerBuilder.Create();
        }

        [TestCleanup]
        public void Cleanup()
        {
            if (serviceProvider is IDisposable d)
            {
                d.Dispose();
            }
        }

        [TestMethod]
        [TestProperty("json", @"{""files"": [{ ""name"": ""1.bmp"", ""rawData"": ""data:image/bmp;base64,""}]}")]
        public void Test_UploadRequest_ShouldBeConstructFileDescriptorWithoutErrors_1()
        {
            var json = TestContext.Properties["json"].ToString();
            var deserializer = serviceProvider.GetRequiredService<IDeserializer>();
            var contentTypeTestUtility = serviceProvider.GetRequiredService<IContentTypeTestUtility>();
            var @object = deserializer.Deserialize<UploadRequest>(json);

            Assert.IsNotNull(@object);
            Assert.IsTrue(@object.Files.Length == 1);

            var data = @object.Files.AsFileDescriptors(contentTypeTestUtility);

            Assert.IsNotNull(data);
            Assert.IsTrue(data.Count() == 1);
        }

