        [TestMethod]
        [TestProperty("json", @"{""links"":[""http://localhost/1.jpg"",""http://localhost/2.bmp""],""files"": [{ ""name"": ""1.bmp"", ""rawData"": ""data:;base64,""}]}")]
        public void Test_UploadRequest_ShouldBeDeserializeLinksWithoutErrors()
        {
            var json = TestContext.Properties["json"].ToString();
            var deserializer = serviceProvider.GetRequiredService<IDeserializer>();
            var @object = deserializer.Deserialize<UploadRequest>(json);

            Assert.IsNotNull(@object);
            Assert.IsTrue(@object.Files.Length == 1);
            Assert.IsTrue(@object.Links.Length == 2);

            Assert.IsTrue(Array.IndexOf(@object.Links, "http://localhost/1.jpg") == 0);
            Assert.IsTrue(Array.IndexOf(@object.Links, "http://localhost/2.bmp") == 1);
        }

        [TestMethod]
        [TestProperty("json", @"{""links"":[""http://localhost/1.jpg"",""http://localhost/2.bmp""],""files"": [{ ""name"": ""1.bmp"", ""rawData"": ""data:image/bmp;charset=US-ASCI,""}]}")]
        [ExpectedException(typeof(ArgumentException))]
        public void Test_UploadRequest_ShouldBeConstructFileDescriptorWithErrors_1()
        {
            var json = TestContext.Properties["json"].ToString();
            var deserializer = serviceProvider.GetRequiredService<IDeserializer>();
            var contentTypeTestUtility = serviceProvider.GetRequiredService<IContentTypeTestUtility>();
            var @object = deserializer.Deserialize<UploadRequest>(json);

            Assert.IsNotNull(@object);
            Assert.IsTrue(@object.Files.Length == 1);
            Assert.IsTrue(@object.Links.Length == 2);
            _ = @object.Files.AsFileDescriptors(contentTypeTestUtility).ToArray();
        }

